// Array to store laptop data
const laptops = [
  { brand: "Dell", model: "Inspiron 15", price: "$500", url: "laptops.html" },
  { brand: "HP", model: "Pavilion 14", price: "$600", url: "hp-pavilion.html" },
  { brand: "Apple", model: "MacBook Pro", price: "$1500", url: "macbook-pro.html" },
  // Add more laptop data here
];

// Function to perform search
function performSearch(event) {
  event.preventDefault(); // Prevent form from submitting and reloading the page

  const query = document.getElementById("searchInput").value.toLowerCase();
  const category = document.getElementById("categorySelect").value;
  
  // Filter laptops based on search query and category
  const filteredLaptops = laptops.filter(laptop => {
    return laptop.brand.toLowerCase().includes(query) || laptop.model.toLowerCase().includes(query);
  });

  displaySearchResults(filteredLaptops);
}

// Display search results
function displaySearchResults(results) {
  const resultsContainer = document.getElementById("searchResults");

  // Clear any previous results
  resultsContainer.innerHTML = "";

  if (results.length > 0) {
    results.forEach(laptop => {
      const resultItem = document.createElement("div");
      resultItem.innerHTML = `
        <a href="${laptop.url}">
          <h3>${laptop.brand} ${laptop.model}</h3>
          <p>Price: ${laptop.price}</p>
        </a>
      `;
      resultsContainer.appendChild(resultItem);
    });
  } else {
    resultsContainer.innerHTML = "<p>No laptops found</p>";
  }
}
